mapper.select:
SELECT M.*, P.MEM_POINT FROM FIG_MEMBER M JOIN (SELECT
MEM_NO, SUM(PO_HISTORY) AS MEM_POINT FROM FIG_POINT GROUP BY MEM_NO) P
ON(M.MEM_NO = P.MEM_NO) WHERE M.MEM_EMAIL = #{memEmail}

SELECT M.MEM_NO, M.MEM_NAME,
M.MEM_PROFILE FROM FIG_RV_LOVES L JOIN FIG_MEMBER M ON(L.MEM_NO =
M.MEM_NO) WHERE L.RV_NO = #{rvNo}

mapper.delete:
DELETE
FIG_FOLLOWING WHERE MEM_NO = #{memNo} AND TARGET_MEM_NO =
#{targetMemNo}

mapper.select:
SELECT * FROM FIG_POINT WHERE MEM_NO = #{memNo}
AND PO_DATE BETWEEN #{startDate} AND TO_DATE(#{endDate}, 'YY-MM-DD') + 1
ORDER BY PO_DATE DESC

SELECT P.*, E.*, R.RES_NAME FROM FIG_EATDEAL_PAY P JOIN FIG_EATDEAL E ON(P.EAT_NO = E.EAT_NO) JOIN FIG_RESTAURANT R ON(E.RES_NO = R.RES_NO) WHERE MEM_NO = #{memNo}
AND BUY_DATE BETWEEN #{startDate} AND TO_DATE(#{endDate}, 'YY-MM-DD') + 1
ORDER BY BUY_DATE DESC

SELECT * FROM FIG_QUESTION WHERE (MEM_NO = #{memNo} OR
TARGET_MEM_NO = #{memNo}) AND
Q_CATEGORY = #{category}
