RES_CLOSE_TIME=#{resCloseTime, typeHandler=strArrRes}, RES_MENU_TITLE=#{resMenuTitle, typeHandler=strArrRes},
RES_MENU_PRICE=#{resMenuPrice, typeHandler=strArrRes}, RES_TEL=#{resTel}, RES_LAT=#{resLat}, RES_LONG=#{resLong}
WHERE RES_NO=#{resNo}

